mapper.update:
UPDATE users
SET email       = #{email},
    password    = #{password},
    name        = #{name},
    phoneNumber = #{phoneNumber}
WHERE id = #{id}

mapper.select:
SELECT id, email, name, password, phoneNumber, addressId
FROM users
WHERE id = #{id}

SELECT id, email, name, password, phoneNumber, addressId
FROM users
WHERE email = #{email}

mapper.update:
UPDATE users
SET addressId = #{addressId}
WHERE id = #{id}
