mapper.select:
select user_address_id, user_id, address_detail, main_address, ST_ASTEXT(address) as address
from user_addresses
where user_address_id = #{id}

select user_address_id, user_id, address_detail, main_address, ST_ASTEXT(address) as address
from user_addresses
where user_id = #{userId}

select user_address_id, user_id, address_detail, main_address, ST_ASTEXT(address) as address
from user_addresses
where user_id = #{userId} and main_address = true;
